export default {
  props: {
    value: Boolean,
    title: String,
    text: String,
    yesButtonTitle: String,
    noButtonTitle: String
  },
  data() {
    return {
      dialog: false
    };
  },
  watch: {
    async dialog(val) {
      this.$emit('input', val);
    },
    async value(val) {
      this.dialog = val;
    }
  },
  methods: {
    async yes() {
      this.$emit('yes');
      this.dialog = false;
    },
    async no() {
      this.$emit('no');
      this.dialog = false;
    }
  }
};
