import ItemFormBase from '@/components/ItemFormBase';
import ArgsPicker from './ArgsPicker.vue';
export default {
  components: {
    ArgsPicker
  },
  mixins: [ItemFormBase],
  computed: {
    isNew() {
      return this.itemId === '';
    }
  },
  data() {
    return {
      id: null
    };
  },
  watch: {
    itemId() {
      this.id = this.itemId;
    }
  },
  methods: {
    setArgs(args) {
      this.item.args = args;
    },
    beforeLoadData() {
      if (!this.isNew) {
        this.id = this.itemId;
      }
    },
    afterReset() {
      this.id = null;
    },
    getItemsUrl() {
      return `/api/apps/${this.id}`;
    },
    getSingleItemUrl() {
      return `/api/apps/${this.id}`;
    }
  }
};
